/**
 * This is the test suite for the node-dstk module.
 *
 *
 * As the Data Science Toolkit distribution uses machine learning techniques some of the test of the
 * type "testing method x - that method returns valid response" might fail for different versions of
 * Data Science Toolkit as the models might have changed.
 *
 *
 * Usage:
 *
 *  $ nodeunit test
 *
 */

/*global exports, require*/


var dstk = require('../lib/dstk'),
	tests = {},
	testName = '';

// Uncomment to use alternative virtual machine for faster processing
//dstk.apiURL = 'http://localhost:8080';


function addTestToTestGroup(testName, testGroup, methodName, testParameters, functionTest) {
	'use strict';

	if (testParameters.parameters) { // If method uses parameters
		testGroup[testName] = function (test) {
			dstk[methodName](testParameters.parameters, function (error, data) {
				functionTest(test, error, data);
			});
		};
	} else { // If method does not use parameters
		testGroup[testName] = function (test) {
			dstk[methodName](function (error, data) {
				functionTest(test, error, data);
			});
		};
	}
}

function addMethodTests(testName, testParameters) {
	'use strict';

	var testGroup = {};

	// Add tests for the method

	exports['testing that method: ' + testName] = testGroup;

	testGroup['exists'] = function (test) {
		test.ok(dstk[testName] instanceof Function);
		test.done();
	};

	testGroup['has a response test function'] = function (test) {
		test.ok(testParameters.test instanceof Function);
		test.done();
	};

	addTestToTestGroup(
		'returns valid response',
		testGroup,
		testName,
		testParameters,
		function (test, error, data) {
			if (error) {
				test.ifError(error);
				test.done();
			} else if (typeof data !== 'object') {
				test.ok(false, 'empty response data');
				test.done();
			} else {
				testParameters.test(test, data);
			}
		}
	);
}


// Define method tests

tests.info = {
	test: function (test, data) {
		'use strict';

		test.ok(typeof data.version === 'number', 'version attribute missing or not a number');
		test.done();
	}
};

tests.text2places = {
	alternative: 'textToPlaces',
	parameters:	'I\'m testing a text that contains the name Stockholm, Sweden, to ensure that ' +
			'places are found in text.',
	test: function (test, data) {
		'use strict';

		test.ok(
			data.length === 1 && data[0].name === 'Stockholm',
			'correct city not found'
		);
		test.done();
	}
};

tests.ip2coordinates = {
	alternative: 'ipToCoordinates',
	parameters: '8.8.8.8',
	test: function (test, data) {
		'use strict';

		test.ok(
			data['8.8.8.8'] && data['8.8.8.8'].locality === 'Mountain View',
			'correct location not found'
		);
		test.done();
	}
};

tests.street2coordinates = {
	alternative: 'streetToCoordinates',
	parameters: '2543 Graystone Place, Simi Valley, CA 93065',
	test: function (test, data) {
		'use strict';

		test.ok(
			data['2543 Graystone Place, Simi Valley, CA 93065'] &&
				data['2543 Graystone Place, Simi Valley, CA 93065'].locality === 'Simi Valley',
			'correct location not found'
		);
		test.done();
	}
};

tests.coordinates2politics = {
	alternative: 'coordinatesToPolitics',
	parameters: '59.332867,18.06450',
	test: function (test, data) {
		'use strict';

		test.ok(
			data[0] && data[0].politics[1] && data[0].politics[1].name === 'Stockholm',
			'correct location not found'
		);
		test.done();
	}
};

tests.text2sentences = {
	alternative: 'textToSentences',
	parameters: 'This sentence contains enough words. So does this one, it appears correct.',
	test: function (test, data) {
		'use strict';

		test.ok(
			data.sentences &&
				data.sentences === 'This sentence contains enough words. So does this one, it appears correct. \n',
			'no or incorrect sentence found'
		);
		test.done();
	}
};

tests.html2text = {
	alternative: 'htmlToText',
	parameters: '<html><head><title>MyTitle</title></head><body><script type="text/javascript">something();</script><div>Some actual text</div></body></html>',
	test: function (test, data) {
		'use strict';

		test.ok(
			data.text && data.text === 'Some actual text\n',
			'no or incorrect text found'
		);
		test.done();
	}
};

